mapper.select:
select tP.nProjectSeq as seq, tP.sTitle as title, tP.sDescription as description, tP.dtStart as start, tP.dtEnd as end, tP.bIsProceeding as isProceeding, tP.nHeadcount as headcount, tP.sMainFunction as mainFunction, tP.sInterest as interest, tP.sGithub as github, tP.bIsPublic as isPublic from tProject tP JOIN tUserProject tUP ON tP.nProjectSeq = tUP.nProjectSeq
and tUP.nUserSeq = #{userSeq}
and tP.bIsPublic = #{isPublic}
