mapper.select:
select nUserSeq
from tUserproject
where nProjectSeq = #{projectSeq}

select sMyPart
from tUserProject
where nUserSeq = #{userSeq}
  and nProjectSeq = #{projectSeq}

mapper.update:
update tUserProject
set sMypart=#{myPart}
where nProjectSeq = #{projectSeq}

mapper.delete:
delete
from tUserProject
where nUserSeq = #{userSeq}
  and nProjectSeq = #{projectSeq}
